
CREATE TABLE `beers` (
  `id` int(11) NOT NULL,
  `brewery_id` int(11) NOT NULL,
  `name` varchar(255) NOT NULL,
  `cat_id` int(11) NOT NULL,
  `style_id` int(11) NOT NULL,
  `abv` decimal(10,0) NOT NULL,
  `ibu` decimal(10,0) NOT NULL,
  `srm` decimal(10,0) NOT NULL,
  `upc` int(11) NOT NULL,
  `filepath` varchar(255) NOT NULL,
  `descript` varchar(1000) NOT NULL,
  `add_user` int(11) NOT NULL,
  `last_mod` date NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=UTF8MB4;



CREATE TABLE `breweries` (
  `id` int(11) NOT NULL,
  `name` varchar(255) NOT NULL,
  `address1` varchar(255) NOT NULL,
  `address2` varchar(255) NOT NULL,
  `city` varchar(255) NOT NULL,
  `state` varchar(255) NOT NULL,
  `code` varchar(255) NOT NULL,
  `country` varchar(255) NOT NULL,
  `phone` varchar(255) NOT NULL,
  `website` varchar(255) NOT NULL,
  `filepath` varchar(255) NOT NULL,
  `descript` varchar(255) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=UTF8MB4;




CREATE TABLE `geocodes` (
  `id` int(4) NOT NULL,
  `brewery_id` int(4) DEFAULT NULL,
  `latitude` decimal(10,8) DEFAULT NULL,
  `longitude` decimal(11,8) DEFAULT NULL,
  `accuracy` varchar(18) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


